import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import axios from 'axios';
import * as firebase from 'firebase';
import * as uuid from 'uuid';
// var uuid = require('uuid');


import Header from './Header';
import Login from './Login';
import Search from './Search';
//import Results from './Results';



class App extends Component {

  constructor() {
    super();
    this.state = {
        uuid: uuid.v1(),
        searchRessults: null,
        searchQoury : null,
        q: null,
        iframToPlay: null,
        userData: null,
        videoTitle: null

    };
    this.searchFun = this.searchFun.bind(this);
    this.play = this.play.bind(this);
    this.toSave = this.toSave.bind(this);
  }

  setSearchQoury = (val) => {
    this.setState({searchQoury: val});
  }

  setQ = (val) => {
    this.setState({q: val});
  }

  play(video){ 
    var link = `https://www.youtube.com/embed/${video.id.videoId}`;
    var iframe =<iframe width="590" height="340" title="myFrame" src={link} frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>;
    var videoTitle = <h3 className="videotitle">{video.snippet.title}</h3>;
    this.setState({iframToPlay: iframe});
    this.setState({videoTitle});
    if(this.state.userData !== null){
      this.toSave(video);
    }

  }


  getData(){
    axios.get(this.state.q)
    .then((response) => {
      const searchRessults = response.data.items;      
      this.setState({searchRessults})
    })
    .catch((error) => {
      console.log(error);
    })
  }  

  setUserData = (user) => {
    this.setState({userData: user});
  }

  toSave = (video) => {
    //console.log(this.state.userData.user);
    firebase.database().ref('youtubeQuery/'+this.state.uuid).set({
        videoID: video.id.videoId,
        videoTitle: video.snippet.title,
        userEmail: this.state.userData.user.email,
        query:this.state.searchQoury
    });

  }


  render() {
    if(this.state.searchRessults !== null){
      var results =         
      <div className="row">
        <h3>Results: {this.state.searchQoury}</h3>
        {
          this.state.searchRessults.map((video, i) => {
            var box = 
            <div className="video-box" key={i}>
              <div className="row">
                <div className="col-md-4 video-img">
                  <img src={video.snippet.thumbnails.medium.url} width="180" />
                </div>
                <div className="col-md-6 video-info">
                  <p><b>Song Name:</b> {video.snippet.title}</p>
                  <p><b>Song description:</b> {video.snippet.description}</p>
                  <p><b>Total votes:</b> {video.snippet.title}</p>
                </div>
                <div className="col-md-2 video-play">
                  <span onClick={() => this.play(video)} >
                    <i className="fa fa-play"></i>
                  </span>
                </div>                  
              </div>
            </div>
            return box;
          })
        }
      </div>
    }
    if(this.state.searchRessults !== null){
      var iframtoplay = this.state.iframToPlay;
      var videoTitle = this.state.videoTitle;
    }

    return (
      <div className="App">
        <Header headerText="Youtube Search" />
        <Login getUserData={this.setUserData}/>
        <Search SearchQoury={this.setSearchQoury} setQ />



        <main className="container">
          <div className="row">
            <div className="col-md-6">
              {results}
            </div>
            <div className="col-md-6">
              <div className="play-box">
                {iframtoplay}
                {videoTitle}
              </div>


            </div>
          </div>

        </main>



        {/*<Results search={this.state.searchRessults}/>*/}
      </div>

    );
  }
}




export default App;
