import React, { Component } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import axios from 'axios';

import Header from './Header';
//import Search from './Search';
//import Results from './Results';

const api ='AIzaSyAie6bUz7eaix7F0xKbkowIlgahHsimPts';
const maxResults = 10;
var apiUrl  = `https://www.googleapis.com/youtube/v3/search?key=${api}&part=snippet,id&maxResults=${maxResults}&type=video&q=`;
class App extends Component {

  constructor() {
    super();
    this.state = {
        searchRessults: null,
        searchQoury : null,
        q: null

    };
    this.searchFun = this.searchFun.bind(this);
    this.play = this.play.bind(this);
  }

  play(val){
    alert()
  }

  searchFun(event){

    this.setState({searchQoury: this.refs.searchQoury.value});
    this.setState({q: apiUrl+this.refs.searchQoury.value});
    console.log
    (this.state.searchQoury);
    console.log
    (this.state.q);
    if(this.state.q !== null){
      this.getData();
    }
    event.preventDefault();
  }


  getData(){
    axios.get(this.state.q)
    .then((response) => {
      console.log(response.data.items);
      const searchRessults = response.data.items;
      // const searchRessults = response.data.items.map(obj => "https://www.youtube.com/embed/"+obj.id.videoId);

      this.setState({searchRessults})
    })
    .catch((error) => {
      console.log(error);
    })
  }  

  render() {
    // if(this.state.searchQoury !== null){
    //   var toSearch = 
    //   <div className="row">
    //     <div className="container">
    //       <div className="col-md-12"> 
    //           <h3>To search: {this.state.searchQoury}</h3>
    //       </div>
    //     </div>
    //   </div>;
    // }

    if(this.state.searchRessults !== null){
      var results =         
      <div className="row">
        <h3>Results: {this.state.searchQoury}</h3>
        {
          this.state.searchRessults.map((video, i) => {
            var box = 
            <div className="video-box" key={i}>
              <div className="row">
                <div className="col-md-4 video-img">
                  <img src={video.snippet.thumbnails.medium.url} width="180" />
                </div>
                <div className="col-md-6 video-info">
                  <p>Song Name: {video.snippet.title}</p>
                  <p>Song description: {video.snippet.description}</p>
                  <p>Total votes: {video.snippet.title}</p>
                </div>
                <div className="col-md-2 video-play">
                  <span onClick={this.play('sss')} >
                    <i className="fa fa-play"></i>
                  </span>
                </div>                  
              </div>
            </div>
            return box;
          })
        }
      </div>
    }

    return (
      <div className="App">
        <Header headerText="Youtube Search" />

        <div className="search-box">
          <div className="container h-100">
            <div className="d-flex justify-content-center h-100">
              <div className="searchbar">
                <form onSubmit={this.searchFun}>
                  <input className="search_input" ref="searchQoury"  type="text" name="" placeholder="Search..." />
                  <button href="#" className="search_icon">
                  <i className="fa fa-search"></i>
                  </button>
                </form>
              </div>
            </div>
          </div>
        </div>
        <main className="container">
          <div className="row">
            <div className="col-md-6">
              {/*toSearch*/}
              {results}
            </div>
            <div className="col-md-6">
              jjjjjj
            </div>
          </div>

        </main>



        {/*<Results search={this.state.searchRessults}/>*/}
      </div>

    );
  }
}




export default App;
