import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './Header';
import Search from './Search';
import Results from './Results';
class App extends Component {

  constructor() {
    super();
    this.state = {
        searchRessults:['movie1','movie2','movie3'],
        searchQoury : ''
    };
  }

  searchFun(){
    this.setState({searchQoury: this.refs.searchQoury.value});
  }


  render() {
    // const myName = "sammy";
    return (
      <div className="App">
        <Header headerText="Youtube Search" />
        <div className="search-box">
          <div className="container h-100">
              <div className="d-flex justify-content-center h-100">
                <div className="searchbar">
                  <input className="search_input" ref="searchQoury"  type="text" name="" placeholder="Search..." />
                  <a onClick={this.searchFun.bind(this)} href="#" className="search_icon">
                    <i className="fa fa-search"></i>
                  </a>

                </div>
              </div>
            </div>
            {this.state.searchQoury}
          </div>

        {this.state.searchQoury}
        <Results search={this.state.searchRessults}/>
      </div>

    );
  }
}




export default App;
