import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import axios from 'axios';

import Header from './Header';
//import Search from './Search';
import Results from './Results';

const api ='AIzaSyAie6bUz7eaix7F0xKbkowIlgahHsimPts';
const maxResults = 20;
var apiUrl  = `https://www.googleapis.com/youtube/v3/search?key=${api}&part=snippet,id&maxResults=${maxResults}&type=video&q=`;
class App extends Component {

  constructor() {
    super();
    this.state = {
        searchRessults:[],
        searchQoury : '',
        q:''

    };
    this.searchFun = this.searchFun.bind(this);

  }

  searchFun(event){
    event.preventDefault();
    this.setState({searchQoury: this.refs.searchQoury.value});
    this.setState({q: apiUrl+this.refs.searchQoury.value});
    if(this.state.q !== ''){
      this.getData();
    }

  }


  getData(){
    axios.get(this.state.q)
    .then((response) => {
      console.log(response.data.items);
      const searchRessults = response.data.items.map(obj => "https://www.youtube.com/embed/"+obj.id.videoId);

      this.setState({searchRessults})
    })
    .catch((error) => {
      console.log(error);
    })
  }  

  render() {
    // const myName = "sammy";
    var toSearch;

    if(this.state.searchQoury !== ''){
      toSearch = 
      <div className="row">
        <div className="container">
          <div className="col-md-12"> 
              <h3>To search: {this.state.searchQoury}</h3>
          </div>
        </div>
      </div>;
    }

    if(this.state.searchRessults !== ''){

    }

    return (
      <div className="App">
        <Header headerText="Youtube Search" />

        <div className="search-box">
          <div className="container h-100">
              <div className="d-flex justify-content-center h-100">
                <div className="searchbar">
                  <form onSubmit={this.searchFun}>
                    <input className="search_input" ref="searchQoury"  type="text" name="" placeholder="Search..." />


                    <button href="#" className="search_icon">
                    <i className="fa fa-search"></i>
                    </button>

                  </form>

                </div>
              </div>
            </div>
          </div>

        {toSearch}


        <div className="row">
        <div className="container">
          <div className="col-md-12"> 
              <h3>To search: {this.state.searchQoury}</h3>
          </div>
        </div>
      </div>


        <Results search={this.state.searchRessults}/>
      </div>

    );
  }
}




export default App;
